        localStorage.setItem("menu-scroll-position", menu.scrollLeft)
    }
    ),
    document.querySelectorAll('a[href^="#"]').forEach(a=>{
        a.addEventListener("click", function(b) {
            b.preventDefault();
            var a = this.getAttribute("href").substr(1);
            window.matchMedia('(prefers-reduced-motion: reduce)').matches ? document.querySelector(`[id='${decodeURIComponent(a)}']`).scrollIntoView() : document.querySelector(`[id='${decodeURIComponent(a)}']`).scrollIntoView({
                behavior: "smooth"
            }),
            a === "top" ? history.replaceState(null, null, " ") : history.pushState(null, null, `#${a}`)
        })
    }
    )    var mybutton = document.getElementById("top-link");
    window.onscroll = function() {
        document.body.scrollTop > 800 || document.documentElement.scrollTop > 800 ? (mybutton.style.visibility = "visible",
        mybutton.style.opacity = "1") : (mybutton.style.visibility = "hidden",
        mybutton.style.opacity = "0")
    }    document.getElementById("theme-toggle").addEventListener("click", ()=>{
        document.body.className.includes("dark") ? (document.body.classList.remove('dark'),
        localStorage.setItem("pref-theme", 'light')) : (document.body.classList.add('dark'),
        localStorage.setItem("pref-theme", 'dark'))
    }
